<?php if (!defined('THINK_PATH')) exit();?> <?php if(is_array($tags)): $i = 0; $__LIST__ = $tags;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><a href="<?php echo (C("rooturl")); ?>read/tag/name/<?php echo (urlencode($vo['name'])); ?>"><span style="float:left;font-size:<?php echo (gettitlesize($vo['id'])); ?>;color:<?php echo (rand_color($$vo['id'])); ?>;margin:2px">  <b><?php echo ($vo['name']); ?><!--[<?php echo ($vo["count"]); ?>]--></b></span></a><?php endforeach; endif; else: echo "" ;endif; ?>